import React from 'react';
import Board from './Board';
import Swal from "sweetalert2";  

class Game extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      // TODO: get number of players in game from database and replace 8
      // also fill the squares with the player names
      squares: this.fillArray(8), 
  
    };
    this.round = 0;
    this.scores = Array(8).fill(0);
    this.gameOver = false;
    this.counter = 0;
    this.answer = 0;
  }

  fillArray(size) {
    var array = Array(size).fill();
    for (var index = 0; index < array.length; index++) {
      array[index] = index;
    }
    return array;
  }

  nextTurn(n) {
    // get next player from api calls
    return Math.floor(Math.random() * n);
  }

  componentDidMount() {
    this.props.pubnub.getMessage(this.props.gameChannel, (msg) => {
      // Publish move to the opponent's board
      
      this.publishMove(msg.message.index, msg.message.piece);

      // Start a new game?
      if(msg.message.reset){
        this.setState({
          // update scores
          squares: this.fillArray(8),
        });

        this.round = 0;
        this.scores = Array(8).fill(0);
        this.gameOver = false;
        this.counter = 0;
        this.answer = 0;
        Swal.close()
      }

      // End the game and go back to the lobby
      else if(msg.message.endGame){
        Swal.close();
        this.props.endGame();
      }
    });
  }

  newGame = (winner) => {

   }

	// Update score for the winner
  updateScore = (winner) => {
    // TODO: Implement function

		// End the game once there is a winner
		// this.gameOver = true;
		// this.newRound(winner);	
  }
  
  checkForWinner = () => {

    // All rounds have been completed
    if (this.round === 4) {
      this.gameOver = true;
      if(this.props.isRoomCreator) {
        Swal.fire({      
              position: 'top',
              allowOutsideClick: false,
              title: 'Game Over',
              text: 'Ready for next round?',
              showCancelButton: true,
              confirmButtonColor: 'rgb(208,33,41)',
              cancelButtonColor: '#aaa',
              cancelButtonText: 'Nope',
              confirmButtonText: 'Yea!',
              width: 275,
            }).then((result) => {
              // Start a new round
              if (result.value) {
                this.props.pubnub.publish({
                  message: {
                    reset: true
                  },
                  channel: this.props.gameChannel
                });
              }
      
              else{
                // End the game
                this.props.pubnub.publish({
                  message: {
                    endGame: true
                  },
                  channel: this.props.gameChannel
                });
              }
            })      
          
      } else {
      Swal.fire({  
            position: 'top',
            allowOutsideClick: false,
            title: 'Game Over',
            text: 'Waiting for a new round...',
            confirmButtonColor: 'rgb(208,33,41)',
            width: 275
          });
        }
    }
  };
   
  // Opponent's move is published to the board
  publishMove = (index, piece) => {
    var squares = this.state.squares;

    squares[index] = piece;

    this.setState({
      // update scores
      squares: squares,
    });

    this.counter += 1;

    // TODO: replace 2 and 8 with number of people in game
    if(this.counter === 2) {
      this.counter = 0;
      squares = Array(8).fill("temp");
      this.round += 1;
    }

    this.round = 0;
    this.scores = Array(8).fill(0);
    this.gameOver = false;
    this.answer = 0;

    this.checkForWinner()
  }

  onMakeMove = (index) => {
    var squares = this.state.squares;

    // Check if the square is empty and if we can make a move
    if(squares[index]) { 
      squares[index] = 'X';

       this.setState({
         squares: squares,
      //   whosTurn: !this.state.whosTurn
       });
  
      // // Other player's turn to make a move
      // this.turn = (this.turn === 'X') ? 'O' : 'X';

      // Publish move to the channel
      this.props.pubnub.publish({
        message: {
          index: index,
          piece: 'X',
          turn: this.turn
        },
        channel: this.props.gameChannel
      });  

      // Check if there is a winner
      this.checkForWinner()
    }
  }

  render() {
    let status;
    // Change to current round
    status = `${this.round}`;

    return (
      <div className="game">
        <div className="prompt">
          Guess who! {this.answer}
        </div>
        <br></br>
        <div className="board">
          <Board
              squares={this.state.squares}
              size={this.props.size}
              onClick={index => this.onMakeMove(index)}
            />  
            <p className="status-info">Round: {status}</p>        
        </div>
        
        <div className="scores-container">
          <div>
            SCORES
          </div>
        </div>   
      </div>
    );
  }
}

export default Game;
